import { html } from 'omni-ui';
import { fixture, expect } from '@open-wc/testing';

import '../src/omni-app-container.js';

describe('OmniAppContainer', () => {
  it('has a default title "omni-app-container"', async () => {
    const el = await fixture(html`<omni-app-container></omni-app-container>`);

    expect(el.title).to.equal('omni-app-container');
  });

  it('passes the a11y audit', async () => {
    const el = await fixture(html`<omni-app-container></omni-app-container>`);

    await expect(el).shadowDom.to.be.accessible();
  });
});
