module.exports = function (file, api, options) {
    const j = api.jscodeshift;

    const deprecatedImports = {
        "OmniToolbarElement":"OmniToolbar",
        "OmniAppLayoutElement":"OmniAppLayout",
        "OmniStyleElement":"OmniStyle",
        "OmniIconElement":"OmniIcon",
        "OmniCardGridElement":"OmniCardGrid",
        "OmniContextProviderElement":"OmniContextProvider",
        "OmniImgUploadElement":"OmniImgUpload",
        "OmniImageInputElement":"OmniImageInput",
        "OmniSearchElement":"OmniSearch",
        "OmniLoadingIndicatorElement":"OmniLoadingIndicator",
        "OmniBadgeElement":"OmniBadge",
        "OmniDialogElement":"OmniDialog",
        "OmniSwitchElement":"OmniSwitch",
        "OmniTableElement": "OmniTable",
        "OmniTileElement":"OmniTile",
        "OmniThemeElement":"OmniTheme",
        "OmniCardElement":"OmniCard",
        "OmniTooltipElement":"OmniTooltip",
        "OmniChipElement":"OmniChip"
    }

    return j(file.source)
    .find(j.ImportDeclaration)
    .forEach(path => {
      let importDec=path.value;
      importDec.specifiers.forEach(specifier=>{
      	if(specifier.imported?.name in deprecatedImports){
          specifier.imported.name = deprecatedImports[specifier.imported.name];
        }
      })
    }).toSource();
}
